import React, { useEffect, useState } from 'react';
import TicketComponent from './TicketComponent';
import { ref, onValue, off } from 'firebase/database';
import {database} from './firebase'; // Adjust the path as necessary

const DashboardComponent = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        const ordersRef = ref(database, 'orders'); // Adjust the reference as needed
        ordersRef.on('value', (snapshot) => {
            const data = snapshot.val();
            const orderList = Object.keys(data).map(key => ({
                ...data[key],
                id: key
            }));
            setOrders(orderList);
        });

        return () => ordersRef.off(); // Unsubscribe on unmount
    }, []);

    return (
        <div>
            {orders.map(order => <TicketComponent key={order.id} order={order} />)}
        </div>
    );
};

export default DashboardComponent;
